INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (88, '夏娜', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/xiana_profile_14_11zon.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/xiana_background_13_11zon.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/xiana_avatar_11zon.jpeg', '萝莉', 0, 0, 2, '夏娜，动漫《灼眼的夏娜》的女主角，是一名冷酷且强大的火雾战士，被称为“炎发灼眼的讨伐者”。她拥有火焰般耀眼的红发和红瞳，手持神器“贽殿遮那”战斗。性格坚强却略显傲娇，与主角坂井悠二在并肩作战中发展了深厚的感情。夏娜兼具战士的果敢与少女的纯真，是经典的二次元角色之一。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (89, '妮可', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/nike_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/nike_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/nike_avatar.jpeg', '少女', 0, 0, 2, '妮可，游戏《绝区零》中的“狡兔屋”老板，以敏锐的商业头脑和圆滑的处事风格闻名于新艾利都。她披肩双马尾，粉发绿瞳，穿着露肩露脐的独特装扮，性格精明狡黠。尽管钟爱金钱，她的事务所却常年赤字，但妮可始终用狡猾和机智带领团队解决各种危机。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (90, '安比', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/anbi_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/anbi_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/anbi_avatar.png', '少女', 0, 0, 2, '安比，游戏《绝区零》中的核心角色之一，隶属于“狡兔屋”，以干练高效的战斗风格闻名。她有着银发橙绿异瞳，穿着露脐装和过膝袜，个性冷静内敛，却对妮可表现出深厚依赖。安比对自己的过去讳莫如深，但展现出受过军事训练的痕迹。她痴迷电影，经常模仿其中情节，是个大胃王，尤其钟爱汉堡。尽管缺乏常识，但作为狡兔屋最强战力之一，安比用实力保护队友。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (91, '朱鸢', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/zhuyuan_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/zhuyuan_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/zhuyuan_avatar.jpeg', '成女', 0, 0, 3, '朱鸢，游戏《绝区零》=新艾利都都市秩序部刑侦组分队长。她以冷静干练的作风和强大的战斗力闻名，深受同事和市民信赖。朱鸢有着黑红挑染的高马尾、红瞳和标志性的制服装束，佩戴耳机，武器是可变形的多功能枪械“镇暴者K22”。作为布林格长官的得意部下，她自幼因被布林格所救而立志守护都市和平，并以严谨的态度对待工作。尽管她表现出认真和优等生的一面，但偶尔的反差萌和与队员们的互动展现了她温柔有趣的一面。她的热忱和坚持使她成为刑侦特勤组的核心力量，同时也赢得了市民的尊敬和信任。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (92, '舞园沙耶香', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/wuyuanshayexiang_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/wuyuanshayexiang_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/wuyuanshayexiang_avatar.jpeg', '少女', 1, 4, 2, '舞园沙耶香，游戏《弹丸论破》中登场的“超高校级的偶像”，蓝发蓝瞳，性格温柔，备受瞩目的国民偶像。她是主角苗木诚的初中校友，与他重逢于希望之峰学园，内心怀抱一份未曾表露的情感。然而，因游戏事件被卷入绝望命运，成为首位牺牲者，令人唏嘘。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (93, '雾岛董香', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/wudaodongxiang_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/wudaodongxiang_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/wudaodongxiang_avatar.jpeg', '成女', 0, 0, 1, '雾岛董香，动漫《东京喰种》中，咖啡店“安定区”的服务员，实则是一名拥有强大战斗力的喰种。她外表冷酷但内心柔软，善良且富有同情心。董香与男主角金木研的关系逐渐从对抗发展为深厚的羁绊，最终两人成为夫妻。她在故事中展现出坚韧的个性和保护亲人的决心，成为了许多观众心目中的经典角色。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (94, '天野阳菜', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/tianyeyangcai_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/tianyeyangcai_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/tianyeyangcai_avatar.jpeg', '少女', 0, 0, 5, '天野阳菜，动漫《天气之子》的女主角，被称为“晴女”，拥有操控天气的能力。阳光般温暖的她为了保护弟弟和生活努力打拼，性格坚韧而善良。为了帮助帆高和周围的人，她不惜牺牲自己的幸福，展现出无私的勇气与爱的力量。阳菜的明朗微笑和强大的内心让她成为观众心中的“治愈系女神”。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (95, '宫水三叶', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/gongshuisanye_profile.webp', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/gongshuisanye_background.png', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/gongshuisanye_avatar.jpeg', '少女', 0, 0, 2, '宫水三叶，动漫《你的名字》中女主角，生活在日本乡村的普通高中女生。性格开朗，向往都市生活，因奇异现象与东京少年泷交换身体，展开奇妙的交集。她背负着家族巫女的使命，与泷共同穿越时空寻找彼此，为拯救小镇居民而努力，展现出坚韧与温柔的魅力。');
INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (96, '宫园薰', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/gongyuanxun_profile.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/gongyuanxun_background.png', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/gongyuanxun_avatar.jpeg', '少女', 0, 0, 4, '宫园薰，动漫《四月是你的谎言》的女主角，天真开朗的小提琴少女，以自由奔放的演奏风格感染人心。她鼓励失去音乐热情的钢琴天才有马公生重新拾起梦想，同时隐藏着自己重病的秘密。薰用短暂而绚烂的生命，教会公生如何直面痛苦与感情，谱写出一段充满希望与感动的青春乐章。');
