INSERT INTO `girls` (`id`, `name`, `girl_src`, `background_src`, `avatar_src`, `age`, `hot`, `average_rate`, `views`, `introduction`) VALUES (99, '芭芭拉', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile/babala_profile.png', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/profile_background/babala_background.jpeg', 'cloud://prod-4guz1brc55a6d768.7072-prod-4guz1brc55a6d768-1330379161/images/avatar/babala_avatar.jpeg', '萝莉', 3, 0, 5, '芭芭拉，游戏《原神》中的角色，西风教会的祈礼牧师，同时也是蒙德城的闪耀偶像。她以歌声和水元素魔法治愈人们的身体与心灵。性格阳光乐观，努力通过自身的方式帮助他人，深受蒙德居民喜爱。她相信“切实的努力是最神奇的魔法”，为实现自己的梦想不断前行。');
